THREE.CubeTexturePass = function ( camera, envMap, opacity ) {

	THREE.Pass.call( this );

	this.camera = camera;

	this.needsSwap = false;

	this.ballsShader = THREE.ShaderLib[ 'balls' ];
	this.ballsMesh = new THREE.Mesh(
		new THREE.BoxBufferGeometry( 10, 10, 10 ),
		new THREE.ShaderMaterial( {
			uniforms: this.ballsShader.uniforms,
			vertexShader: this.ballsShader.vertexShader,
			fragmentShader: this.ballsShader.fragmentShader,
			depthTest: false,
			depthWrite: false,
			side: THREE.BackSide
		} )
	);

	this.envMap = envMap;
	this.opacity = ( opacity !== undefined ) ? opacity : 1.0;

	this.ballsScene = new THREE.Scene();
	this.ballsCamera = new THREE.PerspectiveCamera();
	this.ballsScene.add( this.ballsMesh );

};

THREE.CubeTexturePass.prototype = Object.assign( Object.create( THREE.Pass.prototype ), {

	constructor: THREE.CubeTexturePass,

	render: function ( renderer, writeBuffer, readBuffer, delta, maskActive ) {

		var oldAutoClear = renderer.autoClear;
		renderer.autoClear = false;

		this.ballsCamera.projectionMatrix.copy( this.camera.projectionMatrix );
		this.ballsCamera.quaternion.setFromRotationMatrix( this.camera.matrixWorld );

		this.ballsMesh.material.uniforms[ "tCube" ].value = this.envMap;
		this.ballsMesh.material.uniforms[ "opacity" ].value = this.opacity;
		this.ballsMesh.material.transparent = ( this.opacity < 1.0 );

		renderer.render( this.ballsScene, this.ballsCamera, this.renderToScreen ? null : readBuffer, this.clear );

		renderer.autoClear = oldAutoClear;

	}

} );
